function collision()
	
	for id, weaponInUsage in pairs(weapon) do
		for id2, badFurniture in pairs(furniture) do
			--print(math.ceil(weaponInUsage.y),80,20)
			--print(badFurniture.y,110,20)
			if((math.ceil(weaponInUsage.x)<=badFurniture.x+badFurniture.width and math.ceil(weaponInUsage.x)>=badFurniture.x-badFurniture.width) and (math.ceil(weaponInUsage.y)<=badFurniture.y+badFurniture.heigth and math.ceil(weaponInUsage.y)>=badFurniture.y-badFurniture.heigth)) then
				print("hit!", 150,20)
				table.remove(furniture, id2)
			end
		end
	end

end




function spriteReturn(animation, n, acceleration, n0)

	hangerSpriteNumber = animation[math.ceil((t/acceleration)%n)]

	if(hangerSpriteNumber == nil) then 
		hangerSpriteNumber = n0
	end
	
	return hangerSpriteNumber
end





function playerMovement()
	if btn(0) then
		up=1
		down=0
		right=0
		left=0 
		player.y=player.y-1*inverted 
	end
	if btn(1) then
		up=0
		down=1
		right=0
		left=0  
		player.y=player.y+1*inverted 
	end 
	if btn(3)  then
		up=0
		down=0
		right=1
		left=0 
		player.x=player.x+1*inverted
	end
	if btn(2)  then
		up=0
		down=0
		right=0
		left=1
		player.x = player.x-1*inverted
	end
	checkBounds()

end


function checkBounds()
	if player.x < BORDER_LEFT then
		player.x = BORDER_LEFT
	end
	if player.x > BORDER_RIGHT then
		player.x = BORDER_RIGHT
	end
	if player.y < BORDER_TOP then
		player.y = BORDER_TOP
	end
	if player.y > BORDER_BOT then
		player.y = BORDER_BOT
	end
end

function createWeapons(lastX,lastY)
	dx=lastX-player.x
	dy=lastY-player.y
	if math.abs(dx) >= math.abs(dy) then step = math.abs(dx) else step = math.abs(dy) end
	local newWeapon={
		x=player.x,
		y=player.y-5,
		destX=lastX,
		destY=lastY,
		dx = dx/step,
		dy= dy/step,
		lives=2
		

	}
	
	table.insert(weapon,#weapon+1,newWeapon)
end


function shoot()
	
	
	
	if (mouseVars.pressed and canShoot) then
		--m = calculateIncrement(mouseVars.y,mouseVars.x)
		
		createWeapons(mouseVars.x,mouseVars.y)
		hangerShot = true
		canShoot = false
	end
end

function updateWeapon()
	
	for id, eachWeapon in pairs(weapon) do
		eachWeapon.x=eachWeapon.x + eachWeapon.dx
		eachWeapon.y=eachWeapon.y + eachWeapon.dy
		drawWeapon()
	end
end

function drawWeapon()
	checkWeaponBoundsAndLives()
	
	for id,eachWeapon in pairs(weapon) do
		if eachWeapon.x < midPosLeft and player.isLeftSide then
			spr(spriteReturn(hangerAnimation, 4, 3, hangern0),eachWeapon.x,eachWeapon.y,6,1,0,0,2,2)
			spr(spriteReturn(hangerAnimation, 4, 3, hangern0),239-14-eachWeapon.x,eachWeapon.y,6,1,1,0,2,2)
		elseif (eachWeapon.x > midPosRight and not player.isLeftSide) then
			spr(spriteReturn(hangerAnimation, 4, 3, hangern0),eachWeapon.x,eachWeapon.y,6,1,0,0,2,2)
			spr(spriteReturn(hangerAnimation, 4, 3, hangern0),239-14-eachWeapon.x,eachWeapon.y,6,1,1,0,2,2)
		end
	end
end


function checkWeaponBoundsAndLives()
	for id, eachWeapon in pairs(weapon) do
		if(eachWeapon.y < 0 or eachWeapon.lives==0) then
			table.remove(weapon,id)
		end
	end
end


function drawPlayer()
	if(left==1) then
		spr(spriteReturn(characterSidewaysLeft,4,30,422),player.x,player.y,14,1,0,0,2,2)
		spr(spriteReturn(characterSidewaysLeft,4,30,422),239-16-player.x,player.y,14,1,1,0,2,2)
	elseif(up==1) then
		spr(spriteReturn(characterAnimationUp, 4, 30, 295),player.x,player.y,14,1,0,0,2,2)
		spr(spriteReturn(characterAnimationUp, 4, 30, 295),239-16-player.x,player.y,14,1,1,0,2,2)
	elseif(down==1) then
		spr(spriteReturn(characterAnimationDown, 4, 30, 390),player.x,player.y,14,1,0,0,2,2)
		spr(spriteReturn(characterAnimationDown, 4, 30, 390),239-16-player.x,player.y,14,1,1,0,2,2)
	elseif(right==1) then
		spr(spriteReturn(characterSidewaysRight, 4, 30, 454),player.x,player.y,14,1,0,0,2,2)
		spr(spriteReturn(characterSidewaysRight, 4, 30, 454),239-16-player.x,player.y,14,1,1,0,2,2)
	else
		spr(spriteReturn(characterSidewaysRight, 4, 30, 454),player.x,player.y,14,1,0,0,2,2)
		spr(spriteReturn(characterSidewaysRight, 4, 30, 454),239-16-player.x,player.y,14,1,1,0,2,2)
	end
end

