function drawRightSide()
	for i=0,7 do
		for j=0,15 do
			index=mget(j,i)
			mset(30-j,i,index)
		end
	end
	map(120, 0, 15,7,15,7,1)
end 

function position(object)
	if object.x >= midPosLeft and object.isLeftSide then
		object.isLeftSide = false
		if object.x <= midPosRight then
			object.x = midPosRight + 1
		end
	elseif object.x <= midPosRight and not object.isLeftSide then
		object.isLeftSide = true
		if object.x >= midPosLeft then
			if(object.type == "drawer" or object.type == "wardrobe") then
				object.x = midPosLeft - 17
			else
				object.x = midPosLeft - 1
			end 
		end
	end
end




function createFurniture()

	type_t = ""

	local choice = math.ceil(math.random(0,1))

	if(choice == 0) then 
		type_t = "wardrobe"
	else
		type_t = "drawer"
	end

	yPos = math.random(12, 100)
	

	local newFurniture={
		x=12+(choice*200),
		y=yPos,
		width=16,
		heigth=16,
		type = type_t,
		isLeftSide = true,
	}

	if(t/40%6 == 0) then
		table.insert(furniture,#furniture+1,newFurniture)
	end

end

function updateFurniture()
	for id, eachPiece in pairs(furniture) do
		if(eachPiece.x > player.x and eachPiece.y > player.y) then
			eachPiece.x = eachPiece.x-0.2
			eachPiece.y = eachPiece.y-0.2
		elseif(eachPiece.x < player.x and eachPiece.y < player.y) then
			eachPiece.x = eachPiece.x+0.2
			eachPiece.y = eachPiece.y+0.2
		elseif(eachPiece.x > player.x and eachPiece.y < player.y) then
			eachPiece.x = eachPiece.x-0.2
			eachPiece.y = eachPiece.y+0.2
		elseif(eachPiece.x < player.x and eachPiece.y > player.y) then
			eachPiece.x = eachPiece.x+0.2
			eachPiece.y = eachPiece.y-0.2
		end
		drawFurniture()
	end

end

function positionFurniture()
	for id, eachPiece in pairs(furniture) do
		position(eachPiece)
	end
end


