function drawFurniture()
	
	for id,eachPiece in pairs(furniture) do
		if(eachPiece.type == "wardrobe") then
			if eachPiece.x < midPosLeft and player.isLeftSide then
				spr(spriteReturn(wardrobeAnimation, 2, 12, 384),eachPiece.x - 10,eachPiece.y,14,1,0,0,3,3)
				spr(spriteReturn(wardrobeAnimation, 2, 12, 384),239-12-eachPiece.x,eachPiece.y,14,1,1,0,3,3)
			elseif eachPiece.x > midPosRight and not player.isLeftSide then
				spr(spriteReturn(wardrobeAnimation, 2, 12, 384),eachPiece.x,eachPiece.y,14,1,0,0,3,3)
				spr(spriteReturn(wardrobeAnimation, 2, 12, 384),239-22-eachPiece.x,eachPiece.y,14,1,1,0,3,3)
			end
		elseif (eachPiece.type == "drawer") then
			if eachPiece.x < midPosLeft and player.isLeftSide then
				spr(spriteReturn(drawerAnimation, 2, 15, 432),eachPiece.x - 10,eachPiece.y,14,1,0,0,3,2)
				spr(spriteReturn(drawerAnimation, 2, 15, 432),239-12-eachPiece.x,eachPiece.y,14,1,1,0,3,2)
			elseif eachPiece.x > midPosRight and not player.isLeftSide then
				spr(spriteReturn(drawerAnimation, 2, 15, 432),eachPiece.x,eachPiece.y,14,1,0,0,3,2)
				spr(spriteReturn(drawerAnimation, 2, 15, 432),239-22-eachPiece.x,eachPiece.y,14,1,1,0,3,2)
			end
		end
	end
end

