--game logic
function game()
	
	print(furniture, 84, 84)
	position(player)
	playerMovement()
	positionFurniture()
	shoot()
	updateWeapon()
	updateFurniture()
	draw()
	collision()


	t=t+1
	
	if(hangerShot) then spammingHanger = spammingHanger - 1 end 
	if(spammingHanger == 0) then 
		spammingHanger = 40 
		canShoot = true
		hangerShot = false
	end 
	
	if(t/40%4 == 0) then
		createFurniture()
	end
	
	--print(player.x, 84, 84)
	--if(mouseVars.pressed) then gameState = 3 end

end 

init()

function TIC()
	stateMachine()
	updateMouse()
end


